import * as React from 'react';

import {
  StyleSheet,
  View,
  Text,
  SafeAreaView,
  Button,
  Alert,
} from 'react-native';
import { decode } from '@pagopa/io-react-native-jwt';
import type { JWTDecodeResult } from 'src/types';

export default function App() {
  const [result, setResult] = React.useState<JWTDecodeResult | undefined>();
  const demoJwt =
    'eyJ0eXAiOiJlbnRpdHktc3RhdGVtZW50K2p3dCIsImtpZCI6IkVDIzEiLCJhbGciOiJFUzI1NiJ9..0mfzDKbb1lB6wbhlbtLsmbsJ5Ywc-yrwV4sD9fYoie-ojBBYtXG6qPkcFWj8WQtrguxq5ZGhj7JallDuvYZD8w';

  return (
    <SafeAreaView style={styles.container}>
      <View>
        <Button
          title="Decode JWT"
          onPress={() => decode(demoJwt).then(setResult).catch(showError)}
        />
      </View>
      <View>
        <Text style={styles.title}>{JSON.stringify(result)}</Text>
      </View>
    </SafeAreaView>
  );
}

const showError = (e: any) => {
  Alert.alert('Alert!', JSON.stringify(e), [{ text: 'OK' }]);
};
const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    marginHorizontal: 16,
  },
  title: {
    textAlign: 'center',
    marginVertical: 8,
  },
  fixToText: {
    flexDirection: 'row',
    justifyContent: 'space-between',
  },
  separator: {
    marginVertical: 8,
    borderBottomColor: '#737373',
    borderBottomWidth: StyleSheet.hairlineWidth,
  },
});
