/**
 * Decode the JWT without validation.
 *
 * @example Usage with a public JWK encoded RSA key
 *
 * ```js
 * const jwt =
 *   'eyJ0eXAiOiJlbnRpdHktc3RhdGVtZW50K2p3dCIsImtpZCI6IkVDIzEiLCJhbGciOiJFUzI1NiJ9..6wA0M6rNYNSFN_EylzMA6ElAibW7FVSZyoLNEkHU5c_RKuiNenT08YIMvbysYautLZotUedEMP5xCyNpY34x6Q'
 *
 * const { payload, protectedHeader } = await decode(jwt)
 *
 * ```
 *
 * @param jwt JSON Web Token value (encoded as JWS).
 */
export const decode = (token: string): JWTDecodeResult => SignJWT.decode(token);

/**
 * Verifies the JWS signature
 *
 * @example Usage with a public JWK encoded RSA key
 *
 * ```js
 * const jwk = {
 *   crv: 'P-256',
 *   kty: 'EC',
 *   x: 'qrJrj3Af_B57sbOIRrcBM7br7wOc8ynj7lHFPTeffUk',
 *   y: '1H0cWDyGgvU8w-kPKU_xycOCUNT2o0bwslIQtnPU6iM',
 * }
 * const jwt =
 *   'eyJ0eXAiOiJlbnRpdHktc3RhdGVtZW50K2p3dCIsImtpZCI6IkVDIzEiLCJhbGciOiJFUzI1NiJ9..6wA0M6rNYNSFN_EylzMA6ElAibW7FVSZyoLNEkHU5c_RKuiNenT08YIMvbysYautLZotUedEMP5xCyNpY34x6Q'
 *
 * const isSignatureValid = await verify(jwt, jwk)
 * ```
 *
 * @param jwt JSON Web Token value (encoded as JWS).
 * @param key Public key to verify the JWT in JWK format.
 */
export const isSignatureValid = (token: string, jwk: JWK): Promise<boolean> =>
  IoReactNativeJwt.verify(token, jwk);

/**
 * Verifies the JWT format (to be a JWS Compact format), verifies the JWS signature, validates the
 * JWT Claims Set.
 *
 * @example Usage with a public JWK encoded RSA key
 *
 * ```js
 * const jwk = {
 *   crv: 'P-256',
 *   kty: 'EC',
 *   x: 'qrJrj3Af_B57sbOIRrcBM7br7wOc8ynj7lHFPTeffUk',
 *   y: '1H0cWDyGgvU8w-kPKU_xycOCUNT2o0bwslIQtnPU6iM',
 * }
 * const jwt =
 *   'eyJ0eXAiOiJlbnRpdHktc3RhdGVtZW50K2p3dCIsImtpZCI6IkVDIzEiLCJhbGciOiJFUzI1NiJ9..6wA0M6rNYNSFN_EylzMA6ElAibW7FVSZyoLNEkHU5c_RKuiNenT08YIMvbysYautLZotUedEMP5xCyNpY34x6Q'
 *
 * const { payload, protectedHeader } = await verify(jwt, jwk, {
 *   typ: 'entity-statement+jwt',
 *   requiredClaims: ['iss', 'sub', 'metadata'],
 * })
 * ```
 *
 *  * @example Usage with a JWKSet
 *
 * ```js
 * const wellKnownUrl = 'https://example.com/.well-known/openid-federation';
 * const jwks = await getRemoteJWKSet(wellKnownUrl);
 *
 * const jwt =
 *   'eyJ0eXAiOiJlbnRpdHktc3RhdGVtZW50K2p3dCIsImtpZCI6IkVDIzEiLCJhbGciOiJFUzI1NiJ9..6wA0M6rNYNSFN_EylzMA6ElAibW7FVSZyoLNEkHU5c_RKuiNenT08YIMvbysYautLZotUedEMP5xCyNpY34x6Q'
 *
 * const { payload, protectedHeader } = await verify(jwt, jwks, {
 *   typ: 'entity-statement+jwt',
 *   requiredClaims: ['iss', 'sub', 'metadata'],
 * })
 * ```
 *
 * @param jwt JSON Web Token value (encoded as JWS).
 * @param key Public key to verify the JWT in JWK format or JWKS (array of JWK).
 * @param options JWT Decryption and JWT Claims Set validation options.
 */
export const verify = async (
  token: string,
  jwk: JWK | JWK[],
  options: JWTClaimVerificationOptions = {}
): Promise<JWTDecodeResult> => {
  const { protectedHeader, payload } = await decode(token);

  const selectedJwk = Array.isArray(jwk)
    ? getJwkFromHeader(protectedHeader, jwk)
    : jwk;

  let signatureIsValid = await isSignatureValid(token, selectedJwk);
  if (signatureIsValid) {
    const verifiedPayload = jwtPayload(protectedHeader, payload, options);
    return { payload: verifiedPayload, protectedHeader };
  } else {
    throw new JWSSignatureVerificationFailed();
  }
};

/**
 * Given the header of a JWT return the corresponding key from a JWKS.
 *
 * It uses the "alg" (JWS Algorithm) Header Parameter to determine the right JWK "kty" (Key Type),
 * then proceeds to match the JWK "kid" (Key ID) with one found in the JWS Header Parameters (if
 * there is one).
 * Only a single public key must match the selection process.
 *
 * @param header JWT header
 * @param jwks Json Web Key Set (JWKS)
 *
 */
export const getJwkFromHeader = (
  header: JWSHeaderParameters,
  jwks: JWK[]
): JWK => {
  const { alg, kid } = header;
  if (alg && kid) {
    let kty = getKtyFromAlg(alg);
    let filteredJwks = jwks.find((el) => el.kty === kty && el.kid === kid);

    if (filteredJwks) {
      return filteredJwks;
    }
  }

  throw new JWKNotFound(`No JWKs found that match alg:${alg} and kid:${kid}`);
};
